    public static long getFloor(String s)
    {
        int result = 0;
        for (int i=0; i < s.length(); i++) {
            switch (s.charAt(i))
            {
                case '(': result++; break;
                case ')': result--; break;
            }
            if (result == -1) {
                System.out.println("Floor -1 at position " + (i + 1));
            }
        }
        return result;
    }

